import { Formik } from 'formik';
import { MDBInput, MDBTextArea } from 'mdb-react-ui-kit';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import Swal from 'sweetalert2';

const UpdateData = () => {

    const { id } = useParams();

    const [spaceData, setSpaceData] = useState(null)

    const getSpaceDataById = async () => {
        const res = await fetch('http://localhost:5000/addSpace/getbyid/' + id)
        console.log(res.status)
        if (res.status === 200) {
            const data = await res.json();
            console.log(data)
            setSpaceData(data)
        }
    }

    useEffect(() => {
        getSpaceDataById();
    }, [])

    const update_space_data = async (formdata) => {
        const res = await fetch('http://localhost:5000/addSpace/update/' + id, {
            method: 'PUT',
            body: JSON.stringify(formdata),
            headers: {
                'Content-Type': 'application/json'
            }
        })

        console.log(res.status);

        if (res.status === 200) {
            const data = await res.json();
            Swal.fire({
                icon: 'success',
                title: 'Success',
                text: 'Data Updated Successfully'
            })
            console.log(data)
        }
    }

    const showUpdatedData = () => {
        if (spaceData) {
            return
        }
    }



    return (
        <div className='container'>
            {showUpdatedData()}
        </div>
    )
}

export default UpdateData
